import React, { useEffect } from "react";
import { View, StyleSheet, ScrollView, Alert } from "react-native";
import { Input, Image, Button } from "react-native-elements";
import appContext from "../context/appContext";

function UpdateMe(props) {
  const [firstName, setfirstName] = React.useState(
    props.route.params.firstName
  );
  const [lastName, setlastName] = React.useState(props.route.params.lastName);
  const [email, setEmail] = React.useState(props.route.params.email);
  const [phone, setPhone] = React.useState(props.route.params.phone);

  const handleAdd = (props) => {
    props.setUser({
      email,
      phone,
      firstName,
      lastName,
    });
  };
  return (
    <ScrollView>
      <View style={styles.screen}>
        <Image
          source={{
            uri: "data:image/png;base64,",
          }}
          style={styles.image}
        />
        <Input
          labelStyle={styles.label}
          label="First Name"
          placeholder="Enter First Name"
          value={firstName}
          onChangeText={(text) => setfirstName(text)}
        />
        <Input
          labelStyle={styles.label}
          label="Last Name"
          placeholder="Enter Last Name"
          value={lastName}
          onChangeText={(text) => setlastName(text)}
        />
        <Input
          labelStyle={styles.label}
          keyboardType="number-pad"
          label="Mobile Number"
          placeholder="Enter Phone Number"
          value={phone}
          onChangeText={(text) => setPhone(text)}
        />
        <Input
          labelStyle={styles.label}
          label="E-mail"
          placeholder="Enter E-mail Address"
          value={email}
          onChangeText={(text) => setEmail(text)}
        />
        <Button
          buttonStyle={{ backgroundColor: "#fcacbc", width: 100 }}
          title="Done"
          onPress={handleAdd}
        />
      </View>
    </ScrollView>
  );
}
const styles = StyleSheet.create({
  screen: {
    flex: 1,
    alignItems: "center",
    marginTop: 10,
    padding: 50,
    paddingTop: 10,
  },
  inputContainer: {
    width: 300,
    height: 40,
    margin: 15,
    padding: 10,
  },
  image: {
    borderRadius: 50,
    width: 100,
    height: 100,
    marginBottom: 20,
  },
  label: { color: "black" },
});
export default UpdateMe;
